import React from "react";

export default class Login extends React.Component{
    constructor(props){
        super(props)
        this.state={
            username: "",
            password: "",
            admin: {},
            login_error: ""
        }
    }
    
    handleChange=(e) =>{
        let nm = e.target.name;
        let val = e.target.value;
        this.setState({[nm]: val});
    }

    submitReq=(e)=>{
        e.preventDefault();
        //console.log(this.state);
        const reqData = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                username: this.state.username,
                password: this.state.password
            })
        };

        fetch("http://localhost:8080/login",reqData)
        .then(resp => resp.json())
        .then(data => {if(data.length!=0){
            const json = JSON.parse(data);
            this.setState({admin:JSON});
            localStorage.setItem("loggedinuser",JSON.stringify(this.state.admin));
            //mystore.dispatch({type:'loggedin'})
            this.props.history.push("/Admin");
        }
        else{
            this.setState({login_error: "Wrong Id/Password"});
        }
        })
    }
   
    render(){
        return(
            <div>
              
                  <form>
                      Enter EmailID:
                      <input  type="email" name="emailid" value={this.state.emailid} onChange={this.changeInpText} ></input>
                      <br/>
                      
                      Enter Password:
                      <input  type="password" name="pwd" value={this.state.pwd} onChange={this.changeInpText} ></input>
                      <br/>
                     
                      <input type="submit" value="SUBMIT"  onClick={this.dispMsg} /> 

                  
                  </form>
                
                 
            </div>
        )
    }
    
}
