import {VictoryPie} from 'victory';
import { useEffect, useState } from "react";
import { Link, useLocation } from "react-router-dom";

function ShowProgress(){

    const [tasks, setTasks] = useState([])
    const search = useLocation().search;

    //const [pie1, setPie1] = useState([]);
    //const [pie2, setPie2] = useState([]);
    //const [pie3, setPie3] = useState([]);

    let pie1 = "";
    let pie2 = "";
    let pie3 = "";
    const projectid = new URLSearchParams(search).get('projectid');

    //let sampleData;

    const fetchData = () => {
        fetch("http://localhost:8080/view_status_count?projectid="+projectid)
          .then(response => {
            return response.json()
          })
          .then(data => {
            setTasks(data)
            console.log(data)
            console.log(data[0])
            pie1 = new String(data[0]).split(',');
            //setPie1(data[0]).split(',');
            pie2 = new String(data[1]).split(',');
            pie3 = new String(data[2]).split(',');
            //let count = str.split(',');
            let str = pie1[1];
            console.log(pie1[0]+" : "+pie1[1]);
            console.log(pie2[0]+" : "+pie2[1]);            
            console.log(pie3[0]+" : "+pie3[1]);
          })
    }
    useEffect(() => {
        fetchData()
      }, [])

    const sampleData = [
        {x:pie1[1], y: pie1[0]},
        {x:pie2[1], y: pie2[0]},
        {x:pie3[1], y: pie3[0]}
    ];
return(
    <div className="container" style={{height:600, width: 600}}>
        <VictoryPie
            colorScale={["cyan", "orange", "navy" ]}
            data={[
                {x:pie1[1], y: 5},
                {x:pie2[1], y: 7},
                {x:pie3[1], y: 3}
            ]}
        />
    </div>
);    
}

export default ShowProgress;
