import { useNavigate } from "react-router-dom";
import React, { useEffect, useState } from 'react'
import { MDBBtn, MDBInput } from "mdb-react-ui-kit";
import {
    MDBNavbar,
    MDBNavbarNav,
    MDBNavbarItem,
    MDBNavbarLink,
    MDBNavbarToggler,
    MDBContainer,
    MDBIcon
  } from 'mdb-react-ui-kit';

function UpdateProject(){

    let navigate = useNavigate();
    const[managerid, setManagerId] = useState("");
    const[projectname, setProjectname] = useState("");
    const[project_desc, setProjectdesc] = useState("");
    const[enddate, setEnddate] = useState("");
    const [projects, setProjects] = useState([])

    const fetchData = () => {
        fetch("http://localhost:8080/search_by_projectid?projectid="+projectid)
          .then(response => {
            return response.json()
          })
          .then(data => {
            setProjects(data)
          })
    }

    useEffect(() => {
        console.log(projectid);
        projects.getProjectById(projectid).then((response) =>{
            setManagerId(response.data.managerid)
            setProjectname(response.data.projectname)
            setProjectdesc(response.data.project_desc)
            setEnddate(response.data.enddate)
        }).catch(error => {
            console.log(error)
        })
    }, [])

    const submitForm=(ev)=>{
        ev.preventDefault();

        const reqOptions = {
            method: 'POST',
            headers :{
                'Content-Type':'application/json'
            },
            body: JSON.stringify({
                managerid: managerid,
                projectname: projectname,
                project_desc: project_desc,
                enddate: enddate
            })
        }
        console.log(project_desc);
        fetch("http://localhost:8080/update_project", reqOptions)
        .then(resp =>{
            if(resp.status===200){
                alert("Project updated successfully!");
                navigate("/Admin");
            }
        })
    }

    return(
        <div className="container">
            <header>
            <MDBNavbar expand='lg' light bgColor='white' fixed>
                <MDBContainer fluid>
                <MDBNavbarToggler
