import {VictoryPie} from 'victory';
import { useEffect, useState } from "react";
import { Link, useLocation } from "react-router-dom";

function ShowProgress(){

    const [tasks, setTasks] = useState([])
    const search = useLocation().search;

    const [pie1, setPie1] = useState([]);
    const [pie2, setPie2] = useState([]);
    const [pie3, setPie3] = useState([]);

    const projectid = new URLSearchParams(search).get('projectid');

    const fetchData = () => {
        fetch("http://localhost:8080/view_status_count?projectid="+projectid)
          .then(response => {
            return response.json()
          })
          .then(data => {
            setTasks(data)
            console.log(data)
            console.log(data[0])
            let complete = new String(data[0]).split(',');
            setPie1(data[0]).split(',');
            let todo = new String(data[1]).split(',');
            let inprogress = new String(data[2]).split(',');
            //let count = str.split(',');
            console.log(pie1);
            console.log(todo);
            console.log(inprogress);
          })
    }
    useEffect(() => {
        fetchData()
      }, [])

    const sampleData = [{ x: "Complete", y: 35 },
        { x: "In progress", y: 40 },
        { x: "To do", y: 55 }
    ];
return(
    <div className="container" style={{height:600, width: 600}}>
        <VictoryPie
            colorScale={["tomato", "orange", "navy" ]}
            data={sampleData}
        />
    </div>
);    
}

export default ShowProgress;
