import { Link } from "react-router-dom";
import {
    MDBNavbar,
    MDBNavbarNav,
    MDBNavbarItem,
    MDBNavbarLink,
    MDBNavbarToggler,
    MDBContainer,
    MDBIcon
  } from 'mdb-react-ui-kit';
import { useEffect, useState } from "react";

function Admin() {
    const [projects, setProjects] = useState([])

    const fetchData = () => {
        fetch("http://localhost:8080/show_all_projects")
          .then(response => {
            return response.json()
          })
          .then(data => {
            setProjects(data)
          })
    }
    useEffect(() => {
        fetchData()
      }, [])

    return(
        // <div className="container">
        //     <h1>Welcome Admin</h1>
        //     <Link to="/Registration">Registration</Link> <br/>
        //     <Link to="/CreateProject">Create Project</Link> <br/>
        // </div>
        <div className="container">
        <header>
            <MDBNavbar expand='lg' light bgColor='white' fixed>
                <MDBContainer fluid>
                <MDBNavbarToggler
                    aria-controls='navbarExample01'
                    aria-expanded='false'
                    aria-label='Toggle navigation'
                >
                    <MDBIcon fas icon='bars' />
                </MDBNavbarToggler>
                <div className='collapse navbar-collapse' id='navbarExample01'>
                    <MDBNavbarNav right className='mb-2 mb-lg-0'>
                    <MDBNavbarItem active>
                        <MDBNavbarLink aria-current='page' href='/Admin'>
                        Home
                        </MDBNavbarLink>
                    </MDBNavbarItem>
                    <MDBNavbarItem>
                        <MDBNavbarLink href='/Registration'>Add User</MDBNavbarLink>
                    </MDBNavbarItem>
                    <MDBNavbarItem>
                        <MDBNavbarLink href='#'>Change Password</MDBNavbarLink>
                    </MDBNavbarItem>
                    <MDBNavbarItem>
                        <MDBNavbarLink href='/CreateProject'>Create Project</MDBNavbarLink>
                    </MDBNavbarItem>
                    <MDBNavbarItem>
                        <MDBNavbarLink href='#'>Settings</MDBNavbarLink>
                    </MDBNavbarItem>
                    </MDBNavbarNav>
                </div>
                </MDBContainer>
            </MDBNavbar>
        </header>
        
        <table className="table table-bordered table-hover" style={{border: 'solid'}}>
            <thead>
                <tr>
                <th>Manager id</th>
                <th>Project name</th>
                <th>Project Description</th>
                <th>Start date</th>
                <th>End date</th>
                <th>Client id</th>
                </tr>
            </thead>
            <tbody>
                    {
                        projects.map((projects)=>{
                            return(
                                <tr key={projects.projectid}> 
                                   <td>{projects.managerid}</td> 
                                   <td>{projects.projectname}</td> 
                                   <td>{projects.project_desc}</td> 
                                   <td>{projects.startdate}</td>
                                   <td>{projects.enddate}</td>
                                   <td>{projects.clientid}</td>
                                </tr>
                                ) 
                            })
                    }
            </tbody>
        </table>
        
        </div>
        
    );
    
}
export default Admin;
